        <h5 class="sidebarBlock-heading">{{lang 'category.shop_by_price'}}</h5>
        <ul class="navList">
            {{#each shop_by_price}}
            <li class="navList-item">
                <a class="navList-action {{#if selected }} is-active {{/if}}" href="{{url}}" title="{{low.formatted}} - {{high.formatted}}">{{low.formatted}} - {{high.formatted}}</a>
            </li>
            {{/each}}

            {{#any shop_by_price selected=true}}
                <li class="navList-item">
                    <a href="{{category_url}}" class="navList-action">
                        {{lang 'category.reset'}}
                    </a>
                </li>
            {{/any}}
        </ul>
    </div>
{{/and}}

